const burger=document.querySelector(".burger"),overlay=document.querySelector(".overlay"),headerNav=document.querySelector(".header__top"),navList=document.querySelector(".nav__list"),links=document.querySelectorAll("a");function checkNav(){headerNav.classList.contains("header__top--open")?burger.classList.add("burger--open"):burger.classList.remove("burger--open")}burger.addEventListener("click",(()=>{0===window.scrollY&&burger.classList.toggle("burger--follow"),burger.classList.toggle("burger--follow"),headerNav.classList.toggle("header__top--open"),overlay.classList.toggle("overlay--active"),checkNav()})),overlay.addEventListener("click",(()=>{headerNav.classList.contains("header__top--open")?(headerNav.classList.remove("header__top--open"),overlay.classList.remove("overlay--active")):(headerNav.classList.add("header__top--open"),burger.classList.remove("burger--follow"),overlay.classList.add("overlay--active")),checkNav()}));for(let e of links)e.addEventListener("click",(()=>{headerNav.classList.contains("header__top--open")&&(headerNav.classList.remove("header__top--open"),overlay.classList.remove("overlay--active"),checkNav())}));